con:configuration.scriptText:
import groovy.json.JsonSlurper

def response = messageExchange.response.responseContent

def parsedResponse = new JsonSlurper().parseText(response)


def valueOK = false

def valueRegEx = /^[0-9]*$/

parsedResponse.each{ values ->

     if(values.bookingid==~valueRegEx)
     {
     	valueOK =true
     }


assert valueOK :"Value is not matching"
}
